$(document).ready(function(){
    //prevent form default submition

    //registeration form handling jquery
    $(document).on('submit', '#bs_register_form', function(e){
        //stop default submition
        e.preventDefault();
        e.stopPropagation();
        //get form data 
        var fdata = $(this).serializeArray();
        //make an ajax request for submition of data to server
        $.ajax({
            method: 'POST',
            url: 'server/request/account.request.php',
            data: fdata,
            cache: false,
            dataType: 'JSON',
            beforeSend: function(){
                //show form processing is in progress
                $(document).find('.bs_reg_load').text('Processing registeration...');
            },
            success: function(response){
                //tell client that form has been processed 
                $(document).find('.bs_reg_load').text('Registered Successfully!');
                
                if(response.error !== undefined)
                {
                    $(document).find('.bs_reg_load').html('<span class="text-danger">'+response.error+'</span>');
                }
                else
                {
                    $(document).find('.bs_reg_load').text(response.success);
                }

            },
            error: function(){
                
                $(document).find('.bs_reg_load').html('<span class"text-danger">something when wrong while processing form</span>');
            }

        });
    });

    
});